import Box from './Box';
import React, {useState} from 'react'

function Canvas() {

    function returnRandomColor(box) {

        let existingColor = box.style.backgroundColor;

        let colors = ['blue', 'red', 'green', 'purple', 'pink', 'orange', 'black'];
        colors.pop(existingColor);
        let selectedColor = colors[Math.floor(Math.random() * colors.length)];

        console.log(existingColor, selectedColor);

        return selectedColor;

    }

    function addBox(e) {

        const box = document.createElement('div');
        box.className = 'second';
        box.style.backgroundColor=returnRandomColor(e.target);
        box.onClick=addBox;

        console.log(e.target.getBoundingClientRect());

        e.target.append(box);
    }

    return (
        <div className="first" onClick={addBox}>

        </div>
    )

}

export default Canvas;
