import Box from './Box';
import React, {useState} from 'react'

function Canvas() {

    function returnRandomColor(box) {

        let existingColor = box.style.backgroundColor;
        let allColors = ['blue', 'red', 'green', 'purple', 'pink', 'orange', 'black'];
        let availableColors = []

        for (var i = 0; i++ ; i < allColors.length) {
            console.log('hello')
            if (allColors[i] !== existingColor) {
                availableColors.push(allColors[i]);
            }
        }

        let selectedColor = availableColors[Math.floor(Math.random() * availableColors.length)];

        console.log(selectedColor);

        return selectedColor;

    }

    function addBox(e) {

        const box = document.createElement('div');
        box.className = 'second';
        box.style.backgroundColor=returnRandomColor(e.target);
        box.onClick=addBox;

        console.log(e.target.getBoundingClientRect());

        e.target.append(box);
    }

    return (
        <div className="first" onClick={addBox}>

        </div>
    )

}

export default Canvas;
