import Box from './Box';
import React, {useState} from 'react'

function Canvas() {

    function returnRandomColor(argClass, argColor) {

        let existingColor = ''

        if (argClass === 'first') {
            existingColor = 'red';
        } else {
            existingColor = argColor;
        }

        const allColors = ['blue', 'green', 'purple', 'pink', 'orange', 'black', 'red'];
        const availableColors = [];

        for (let i = 0; i < allColors.length; i++) {
            if (allColors[i] !== existingColor) {
                availableColors.push(allColors[i]);
            }
        }

        let newColor = availableColors[Math.floor(Math.random() * availableColors.length)];
        console.log(existingColor, newColor);

        return newColor;
    }


    function getRelativePosition(mouseX, mouseY, boundingData) {
        console.log(mouseX, mouseY, boundingData);

        console.log(boundingData.DOMRect);
    }


    function addBox(e) {

        const box = document.createElement('div');
        box.className = 'second';
        box.style.backgroundColor=returnRandomColor(e.target.className, e.target.style.backgroundColor);
        let relativePosition = getRelativePosition(e.clientX, e.clientY, e.target.getBoundingClientRect());
        box.onClick=addBox;

        console.log(relativePosition);

        e.target.append(box);
    }

    return (
        <div className="first" onClick={addBox}>

        </div>
    )

}

export default Canvas;
