import Box from './Box';
import React, {useState} from 'react'

function Canvas() {

    function returnRandomColor(box) {

        let existingColor = '';
        if (box.style.backgroundColor) {
            existingColor = box.style.backgroundColor;
        } else {
            existingColor = 'red';
        }

        let allColors = ['blue', 'red', 'green', 'purple', 'pink', 'orange', 'black'];

        console.log(existingColor);

        for (var i = 0; i++ ; i < allColors.length) {
            console.log(i);
            if (allColors[i] !== existingColor) {
                allColors.push(allColors[i]);
            }
        }

        let selectedColor = allColors[Math.floor(Math.random() * allColors.length)];

        return selectedColor;

    }

    function addBox(e) {

        const box = document.createElement('div');
        box.className = 'second';
        box.style.backgroundColor=returnRandomColor(e.style.backgroundColor);
        box.onClick=addBox;

        console.log(e.target.getBoundingClientRect());

        e.target.append(box);
    }

    return (
        <div className="first" onClick={addBox}>

        </div>
    )

}

export default Canvas;
